    function time_ago( ptime ) {
        var ddd = new Date;
		var etime = ddd.getTime() / 1000 - 1800 - ptime;
		if(etime < 1) 
			return '刚刚';

		var d = etime / 31104000;
		if (d >= 1) {
			var r = Math.round(d);
			return r +  ' 年前';
		}
		d = etime / 2592000;
		if (d >= 1) {
			var r = Math.round(d);
			return r +  ' 个月前';
		}
		d = etime / 604800;
		if (d >= 1) {
			var r = Math.round(d);
			return r + ' 周前';
		}
		d = etime / 86400;
		if (d >= 1) {
			var r = Math.round(d);
			return r + ' 天前';
		}
		d = etime / 3600;
		if (d >= 1) {
			var r = Math.round(d);
			return r +  ' 小时前';
		}
		d = etime / 60;
		if (d >= 1) {
			var r = Math.round(d);
			return r + ' 分钟前';
		}
		d = etime / 1;
		if (d >= 1) {
			var r = Math.round(d);
			return r + ' 秒前';
		}
	}

